(()=>{return(e,i)=>{const a=`<div class=minimal-home><div class=minimal-home-header><icon class=logo icon=logo type=main></icon><div class=home-tabs><div v-for="(tab, index) of tabs":key=index class=tab :class="{active: tab.active}":data-tab=tab.name @click=changeTab(tab)>{{tab.displayName}}</div></div><search></search></div><div class=minimal-home-content><component :is=content></component></div></div>`;i.applyStyleFromText(`.minimal-home,.minimal-home *{box-sizing:border-box}.minimal-home .logo{font-size:48px;color:var(--theme-color)}.minimal-home .minimal-home-header{display:flex;align-items:center;justify-content:space-between}.minimal-home .minimal-home-header .home-tabs{display:flex;flex-grow:1;justify-content:space-around}.minimal-home .minimal-home-content{margin-top:64px;width:calc(var(--card-column-count) * (var(--card-width) + var(--card-margin)))}`,"minimal-home-style");return{export:Object.assign({template:a},{components:{Icon:()=>i.importAsync("icon.vue"),Search:()=>i.importAsync("search.vue"),HomeVideo:()=>i.importAsync("home-video.vue")},data(){return{tabs:[{name:"video",displayName:"视频动态",active:true,more:"https://t.bilibili.com/?tab=8"},{name:"ranking7",displayName:"一周排行",active:false,more:"https://www.bilibili.com/ranking/all/0/0/7"},{name:"ranking3",displayName:"三日排行",active:false,more:"https://www.bilibili.com/ranking"},{name:"ranking1",displayName:"昨日排行",active:false,more:"https://www.bilibili.com/ranking/all/0/0/1"}],content:"HomeVideo"}},methods:{changeTab(e){if(e.active){window.open(e.more,"_blank");return}const i=this.tabs.find(e=>e.active);i.active=false;e.active=true}}})}}})();